const Largecard = (props) => {
    //pill generation
    var toolArray = props.tools.split(',');
    var languageArray = props.languages.split(',');
    var tools = [];
    var languages = [];
    for (var i=0; i<toolArray.length; i++){
        if (i===0){
            tools.push(<div className="first secondaryPill" >{toolArray[i]}</div>);
        } else if (i===toolArray.length){
            tools.push(<div className="secondaryPill">{toolArray[i]}</div>);
        } else {
            tools.push(<div className="last secondaryPill">{toolArray[i]}</div>);
        }
    }
    for (var i=0; i<languageArray.length; i++){
        if (i===0){
            languages.push(<div className="first mainPill">{languageArray[i]}</div>);
        } else if (i===toolArray.length){
            languages.push(<div className="mainPill">{languageArray[i]}</div>);
        } else {
            languages.push(<div className="last mainPill">{languageArray[i]}</div>);
        }
    }

    return (
    <>
        <div className="card bound nopad my-container max531" style={{borderRadius: '12px',}}>
            <div className="card-body nopad ">
                <div className="row">
                    <div className="col-md-8 align-self-center" style={{paddingLeft: '8%',paddingRight: '8%',}}>
                        <div style={{height:'32px',width:'100%'}}></div>
                        <p>{props.status}</p>
                        <h1>{props.title}</h1>
                        <p>{props.description}</p>
                        <div style={{height:'12px',width:'100%'}}></div>
                        {languages}
                        <div style={{height:'0px',width:'100%'}}></div>
                        {tools}
                        <div style={{height:'32px',width:'100%'}}></div>
                    </div>
                    {console.log(this)}
                    <div className="col nopad" style={{border:'1px solid white'}}>
                        <div className="max531">
                            <img src={require(`../assets/${props.imageName}`)} className="img-fluid nopad" style={{borderRadius: '0px 12px 12px 0px'}}/>
                            <img src={require(`../assets/${props.imageNameM}`)} className="img-fluid nopad" style={{borderRadius: '0px 12px 12px 0px'}}/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div style={{height:'64px',width:'100%'}}></div>
    </>
    )
}

export default Largecard
