//this intro doesnt really need to be changed
import thatme from '../assets/pilly.png'

const Largecard = (props) => {
    //pill generation
    var toolArray = props.tools.split(',');
    var languageArray = props.languages.split(',');
    var tools = [];
    var languages = [];
    for (var i=0; i<toolArray.length; i++){
        if (i===0){
            tools.push(<div className="first" style={secondaryPill}>{toolArray[i]}</div>);
        } else if (i===toolArray.length){
            tools.push(<div style={secondaryPill}>{toolArray[i]}</div>);
        } else {
            tools.push(<div className="last" style={secondaryPill}>{toolArray[i]}</div>);
        }
    }
    for (var i=0; i<languageArray.length; i++){
        if (i===0){
            languages.push(<div className="first" style={mainPill}>{languageArray[i]}</div>);
        } else if (i===toolArray.length){
            languages.push(<div style={mainPill}>{languageArray[i]}</div>);
        } else {
            languages.push(<div className="last" style={mainPill}>{languageArray[i]}</div>);
        }
    }

    return (
    <>
        <div className="card bound nopad" style={{maxHeight:'531px', borderRadius: '12px',}}>
            <div className="card-body nopad ">
                <div className="row">
                    <div className="col-md-8 align-self-center" style={{paddingLeft: '8%',paddingRight: '8%',}}>
                        <div style={{height:'32px',width:'100%'}}></div>
                        <p>{props.status}</p>
                        <h1>{props.title}</h1>
                        <p>{props.description}</p>
                        <div style={{height:'12px',width:'100%'}}></div>
                        {languages}
                        <div style={{height:'0px',width:'100%'}}></div>
                        {tools}
                        <div style={{height:'32px',width:'100%'}}></div>
                    </div>
                    {console.log(this)}
                    <div className="col nopad">
                        <div style={{maxHeight:'531px'}}>
                            <img src={require(`../assets/${props.name}.png`)} className="img-fluid nopad" style={{borderRadius: '0px 12px 12px 0px'}}/>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        <div style={{height:'64px',width:'100%'}}></div>
    </>
    )
}

const mainPill={
    backgroundColor: 'white',
    border: 'none',
    color: '#651FFF',
    padding: '6px 12px',
    textAlign: 'center',
    display: 'inline-block',
    margin: '4px 3px',
    borderRadius: '8px',
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: '18px',
    lineHeight: '21px',
}

const secondaryPill={
    backgroundColor: 'white',
    border: 'none',
    color: 'black',
    padding: '6px 12px',
    textAlign: 'center',
    display: 'inline-block',
    margin: '4px 3px',
    borderRadius: '8px',
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: '18px',
    lineHeight: '21px',
}

export default Largecard
