//this intro doesnt really need to be changed
import thatme from '../assets/pilly.png'

const Largecard = (props) => {
    //pill generation
    var toolArray = props.tools.split(',');
    var languageArray = props.languages.split(',');
    var tools = [];
    var languages = [];
    for (var i=0; i<toolArray.length; i++){
        if (i===0){
            tools.push(<div className="first" style={secondaryPill}>{toolArray[i]}</div>);
        } else if (i===toolArray.length){
            tools.push(<div style={secondaryPill}>{toolArray[i]}</div>);
        } else {
            tools.push(<div className="last" style={secondaryPill}>{toolArray[i]}</div>);
        }
    }
    for (var i=0; i<languageArray.length; i++){
        if (i===0){
            languages.push(<div className="first" style={mainPill}>{languageArray[i]}</div>);
        } else if (i===toolArray.length){
            languages.push(<div style={mainPill}>{languageArray[i]}</div>);
        } else {
            languages.push(<div className="last" style={mainPill}>{languageArray[i]}</div>);
        }
    }

    return (
        <div className="card bound nopad" style={{maxHeight:'531px'}}>
            <div className="card-body nopad ">
                <div className="row"  style={{paddingLeft:'4vw',}}>
                    <div className="col-md-7 nopad my-col align-self-center">
                        <p style={statusStyle}>{props.status}</p>
                        <p style={titleStyle}>{props.title}</p>
                        <p style={descStyle}>{props.description}</p>
                        <div style={{height:'24px',width:'100%'}}></div>
                        {languages}
                        <div style={{height:'0px',width:'100%'}}></div>
                        {tools}
                    </div>
                    <div className="col" style={{minHeight:'100%'}}>
                        <img src={thatme} className="img-fluid" style={{minHeight:'100%'}}/>
                    </div>
                </div>
            </div>
        </div>
    )
}

const mainPill={
    backgroundColor: 'white',
    border: 'none',
    color: '#651FFF',
    padding: '6px 12px',
    textAlign: 'center',
    display: 'inline-block',
    margin: '4px 3px',
    borderRadius: '8px',
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: '18px',
    lineHeight: '21px',
}

const secondaryPill={
    backgroundColor: 'white',
    border: 'none',
    color: 'black',
    padding: '6px 12px',
    textAlign: 'center',
    display: 'inline-block',
    margin: '4px 3px',
    borderRadius: '8px',
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'bold',
    fontSize: '18px',
    lineHeight: '21px',
}

const statusStyle={
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'normal',
    fontSize: '18px',
    color: 'black',
}

const titleStyle={
    fontFamily: 'Rubik',
    fontStyle: 'normal',
    fontWeight: 'Bold',
    fontSize: '64px',
    color: 'black',
}

const descStyle={
    fontFamily: 'Karla',
    fontStyle: 'normal',
    fontWeight: 'normal',
    fontSize: '24px',
    color: 'black',
}

export default Largecard
