const Workcard = (props) => {
    //pill generation
    var toolArray = props.tools.split(',');
    var tools = [];
    for (var i=0; i<toolArray.length; i++){
        if (i===0){
            tools.push(<div className="first secondaryPillsm" >{toolArray[i]}</div>);
        } else if (i===toolArray.length){
            tools.push(<div className="secondaryPillsm">{toolArray[i]}</div>);
        } else {
            tools.push(<div className="last secondaryPillsm">{toolArray[i]}</div>);
        }
    }

    bgcolor=props.barcolor;

    return (
    <>
    <div className="col-sm-6 nopad" style={{paddingLeft:'28px',paddingRight:'28px',paddingBottom:'64px'}}>
        <div className="card nopad" style={{borderRadius: '12px',}}>
            <div className="card-body smallBound nopad ">
                <div style={{backgroundColor:bgcolor}} class="workcardtop"></div>
                <div className="row align-items-start h384" style={{paddingRight:'10%',paddingLeft:'10%'}}>
                        <div style={{height:'32px',width:'100%'}}></div>
                        <div className="row" style={{width:'100%',paddingBottom:"12px"}}>
                            <p className="nopad"><b style={{color:'black'}}>{props.title}</b><br/>{props.time}</p>
                        </div>
                        <h3 style={{paddingBottom:"12px"}}><b style={{color:'black'}}>{props.company}</b></h3>
                        <h6 style={{paddingBottom:"24px"}}>{props.description}</h6>
                        {tools}
                        <div style={{height:'32px',width:'100%'}}></div>
                </div>
            </div>
        </div>
    </div>
    </>
    )
}

export default Workcard
